import React, { useState, useEffect } from 'react';
import { Button, Select, Input, Card, DatePicker, Space, Divider, Image } from 'antd';
import styles from '../style.less';
// import Delivery from './delivery';


const Block = (props) => {
  const {info,setVisibleSecondData} = props

  const editspecAttr=()=>{
    setVisibleSecondData({
        specAttrStatus:info.specAttrStatus || '1',
        specAttrName:info.specAttrName,
        specId:info.specId,
        specAttrId:info.specAttrId,
        opType:'2'
    })
  }
  return (
    <div className={styles.block}>
      <Image
        width={100}
        src={info.logo}
        fallback="data:image/png;base64,"

      />

      <div className={styles.block_edit}>
        <div className={styles.block_edit_t}>
          <div className={styles.block_edit_t_title}>{info.categoryName}</div>
          <div className={styles.block_edit_t_icon_con}>
            <div className={styles.card_title_icon}>禁</div>
          </div>
          <Divider type="vertical" />
          <div className={styles.block_edit_opa} onClick={editspecAttr}>修改</div>
        </div>
        <div className={styles.block_edit_b}>
          <Space>
            <div>spu:{info.spuNum}</div>
            <div>sku:{info.skuNum}</div>
          </Space>
        </div>
      </div>
    </div>
  );
};

const CardTitle = (title,state) => {
  return (
    <div className={styles.card_title}>
      <div>{title}</div>
      <div className={styles.card_title_icon}>禁</div>
    </div>
  );
};

const BlockList = (props) => {
  const {detail,setVisibleData,setVisibleSecondData}= props
  const {categoryName,childrenCategoryList=[] } = detail
  const editSpec=()=>{
    setVisibleData({
      visible: true,
      record: {
        specStatus:detail.specStatus || '1',
        specName:detail.specName,
        specId:detail.specId,
        opType:'2'
      },
    })
  } 
  return (
    <Card
      title={CardTitle(categoryName)}
      bordered={false}
      extra={<span className={styles.block_edit_opa} onClick={editSpec}>修改</span>}
    >
      <Space style={{ flexWrap: 'wrap' }}>
        {
          childrenCategoryList.map((item,index)=>{
            return <Block info={item} key={index} setVisibleSecondData={setVisibleSecondData}/>
          })
        }

      </Space>
      {/* <Delivery visibleData={visibleData} setVisibleData={setVisibleData} /> */}
    </Card>
  );
};

export default BlockList;
